export interface IGroupingItemInfo {
    /** Идентификатор столбца, по значению которого осуществляется группировка. */
    columnId: string;
    /** Уровень группировки, отсчитываемый с 0. */
    level: number;
    /** Отображаемое название пункта в интерфейсе. */
    name: string;
    /** Исходное значение группировки. */
    value: unknown;
    /** Количество карточек в группе. */
    count: number;
    /** Родительского значение группировки вышестоящего уровня. */
    parent?: IGroupingItemInfo;
}
