/// <reference types="react" />
import { ICompositionPlugins } from './ICompositionPlugins';
export interface IComposition<CompositionT extends string = any, DataT = unknown, ServicesT = unknown> {
    readonly name: string;
    readonly plugins: ICompositionPlugins<CompositionT, DataT, ServicesT>;
    readonly services: ServicesT;
    readonly data: DataT;
    readonly ref: HTMLElement;
    readonly disabledFeatures: string[];
    getChildrenData(): unknown[];
    renderChild(childData: unknown): React.ReactNode;
    renderChildren(childrenData?: unknown[]): React.ReactNode;
    update(): Promise<void>;
}
