/** Результат валидации контрола. */
export interface IValidationResult {
    /** Валидация была успешно пройдена. */
    Passed: boolean;
    /** Сообщение об ошибке. */
    Message?: string;
    /** Имя контрола, сгенерировавшего объект. */
    ControlName?: string;
    /** Флаг видимости контрола. */
    Visibility?: boolean;
}
