export interface IWebServicesRequest<T> {
    /** Действие плагина, которое необходимо выполнить. */
    action?: string;
    /** JavaScript объект, содержащий параметры запроса */
    data?: T;
    /** Двухбуквенный ISO-код культуры. */
    locale: string;
    /** Закрывать ли DVWebTool после вызова. */
    exitAfterCall: boolean;
}
